import React, { useState, useRef, useEffect } from "react";
import axios from "axios";

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // Handle File Selection (Mobile + Laptop)
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    setImagePreview(URL.createObjectURL(file));
  };

  // Handle Upload to Backend
  const handleUpload = async () => {
    if (!selectedFile) {
      console.error("No file selected");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    console.log("Sending request to backend...");

    fetch("http://127.0.0.1:8000/caption/", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => console.log("Caption:", data.caption))
      .catch((error) => console.error("Error:", error));
  };


  // Start Camera (Laptop Webcam)
const startCamera = async () => {
    try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        streamRef.current = stream;

        // Wait for React to render the video element
        setCameraActive(true);

        setTimeout(() => {
            if (videoRef.current) {
                videoRef.current.srcObject = stream;
                videoRef.current.play();
            } else {
                console.error("Video element not found.");
            }
        }, 500); // Adding a delay to ensure rendering
    } catch (error) {
        console.error("Error accessing camera:", error);
    }
};


  // Stop Camera
  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
    }
    setCameraActive(false);
  };

  // Capture Image from Webcam
  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) {
      console.error("Camera is not active.");
      return;
    }

    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(blob)); // Preview the captured image
    }, "image/jpeg");
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>VisionMate - Image Captioning</h2>

      {/* Mobile: File Upload & Open Camera */}
      <input type="file" accept="image/*" capture="environment" onChange={handleFileChange} />
      <button onClick={handleUpload} style={{ marginLeft: "10px" }}>Generate Caption</button>

      {/* Laptop: Webcam Access */}
      <div style={{ marginTop: "20px" }}>
        {!cameraActive ? (
          <button onClick={startCamera}>Open Camera (Laptop)</button>
        ) : (
          <>
            <video ref={videoRef} autoPlay width="400" height="300"></video>
            <canvas ref={canvasRef} width="400" height="300" style={{ display: "none" }}></canvas>
            <button onClick={captureImage} style={{ marginLeft: "10px" }}>Capture Image</button>
            <button onClick={stopCamera} style={{ marginLeft: "10px" }}>Close Camera</button>
          </>
        )}
      </div>

      {/* Display Image Preview */}
      {imagePreview && (
        <div style={{ marginTop: "20px" }}>
          <h4>Preview:</h4>
          <img src={imagePreview} alt="Selected or Captured" width="300" />
        </div>
      )}

      {/* Caption Display */}
      {caption && <p style={{ marginTop: "20px", fontSize: "18px" }}>Caption: {caption}</p>}
    </div>
  );
}

export default App;
