import React, { useState, useRef, useEffect } from "react";

function App() {
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // Update this URL to your actual backend
  const BACKEND_URL = "http://192.168.1.108:8000/caption/";

  // Check if device is mobile
  const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);

  // Clean up function to stop camera
  const cleanupCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => {
        track.stop();
      });
      streamRef.current = null;
    }
  };

  // Cleanup on unmount
  useEffect(() => {
    return () => cleanupCamera();
  }, []);

  const playSound = (src) => {
    try {
      const audio = new Audio(src);
      audio.play().catch(() => {});
    } catch (error) {
      console.log("Audio play error:", error);
    }
  };

  const speak = (message) => {
    if ('speechSynthesis' in window) {
      try {
        const utterance = new SpeechSynthesisUtterance(message);
        speechSynthesis.speak(utterance);
      } catch (error) {
        console.log("Speech synthesis error:", error);
      }
    }
  };

  const generateCaption = async (blob) => {
    setLoading(true);
    setError(null);

    const formData = new FormData();
    const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
    formData.append("file", file);

    try {
      const response = await fetch(BACKEND_URL, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error(`Server error: ${response.status}`);

      const data = await response.json();
      setCaption(data.caption);
      speak(`Caption: ${data.caption}`);
    } catch (error) {
      setError("Failed to generate caption. Please try again.");
      speak("Error generating caption.");
      console.error("Upload error:", error);
    } finally {
      setLoading(false);
    }
  };

  const startCamera = async () => {
    // First, clean up any existing stream
    cleanupCamera();
    setError(null);

    try {
      const constraints = {
        video: {
          facingMode: isMobile ? "environment" : "user",
          width: { ideal: 1280 },
          height: { ideal: 720 }
        },
        audio: false
      };

      console.log("Requesting camera with constraints:", constraints);
      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      console.log("Camera stream obtained");

      streamRef.current = stream;

      // Make sure video element exists
      if (videoRef.current) {
        videoRef.current.srcObject = stream;

        // Set up event listeners for the video element
        videoRef.current.onloadedmetadata = () => {
          console.log("Video metadata loaded");
          videoRef.current.play()
            .then(() => {
              console.log("Video playing");
              setCameraActive(true);
            })
            .catch(err => {
              console.error("Video play error:", err);
              setError("Could not play video stream.");
              cleanupCamera();
            });
        };

        videoRef.current.onerror = (err) => {
          console.error("Video element error:", err);
          setError("Video element error.");
          cleanupCamera();
        };
      } else {
        console.error("Video element not found");
        setError("Video element not found.");
        cleanupCamera();
      }
    } catch (error) {
      console.error("Camera access error:", error);
      setError("Could not access camera. Please check browser permissions.");
      cleanupCamera();
    }
  };

  const stopCamera = () => {
    cleanupCamera();
    setCameraActive(false);

    // Clear video source
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) {
      console.error("Video or canvas ref not available");
      setError("Could not capture image. Please try again.");
      return;
    }

    try {
      const video = videoRef.current;
      const canvas = canvasRef.current;

      // Get the actual video dimensions
      const videoWidth = video.videoWidth;
      const videoHeight = video.videoHeight;

      if (videoWidth === 0 || videoHeight === 0) {
        console.error("Video dimensions are zero");
        setError("Cannot capture from camera. Please restart the camera.");
        return;
      }

      console.log(`Capturing image with dimensions: ${videoWidth}x${videoHeight}`);

      // Set canvas dimensions to match video
      canvas.width = videoWidth;
      canvas.height = videoHeight;

      // Draw the video frame to the canvas
      const ctx = canvas.getContext('2d');
      ctx.drawImage(video, 0, 0, videoWidth, videoHeight);

      // Convert canvas to blob
      canvas.toBlob((blob) => {
        if (blob) {
          console.log("Image captured successfully");
          const imageUrl = URL.createObjectURL(blob);
          setImagePreview(imageUrl);
          setCaption("");
          generateCaption(blob);
        } else {
          console.error("Failed to create blob from canvas");
          setError("Failed to process captured image.");
        }
      }, 'image/jpeg', 0.95);
    } catch (error) {
      console.error("Image capture error:", error);
      setError("Failed to capture image: " + error.message);
    }
  };

  // Handle keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === "c") startCamera();
      else if (event.key === "Escape") stopCamera();
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div className="container" style={styles.container}>
      <h1 style={styles.header}>📸 VisionMate</h1>

      {error && (
        <div style={styles.errorBox}>
          <p style={styles.errorText}>⚠️ {error}</p>
        </div>
      )}

      <div style={styles.cameraContainer}>
        {!cameraActive ? (
          <button 
            onClick={startCamera} 
            style={styles.button}
          >
            📷 Open Camera
          </button>
        ) : (
          <>
            <video 
              ref={videoRef} 
              autoPlay 
              playsInline 
              style={styles.video} 
            />
            <div style={styles.buttonContainer}>
              <button 
                onClick={captureImage} 
                style={styles.button}
                disabled={loading}
              >
                {loading ? "Processing..." : "📸 Capture Image"}
              </button>
              <button 
                onClick={stopCamera} 
                style={{...styles.button, backgroundColor: "#ff4d4d"}}
              >
                ❌ Close Camera
              </button>
            </div>
          </>
        )}
      </div>

      {/* Hidden canvas for image processing */}
      <canvas ref={canvasRef} style={{ display: 'none' }} />

      {imagePreview && (
        <div style={styles.previewContainer}>
          <h3 style={styles.sectionHeader}>Image Preview</h3>
          <img 
            src={imagePreview} 
            alt="Captured" 
            style={styles.previewImage} 
          />
        </div>
      )}

      {caption && (
        <div style={styles.captionContainer}>
          <h3 style={styles.sectionHeader}>Caption</h3>
          <p style={styles.captionText}>{caption}</p>
        </div>
      )}
    </div>
  );
}

// Styles
const styles = {
  container: {
    padding: '15px',
    maxWidth: '600px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
  },
  header: {
    textAlign: 'center',
    color: '#007bff',
    marginBottom: '20px',
  },
  cameraContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    width: '100%',
  },
  video: {
    width: '100%',
    maxWidth: '480px',
    height: 'auto',
    borderRadius: '8px',
    border: '2px solid #000',
    marginBottom: '10px',
  },
  buttonContainer: {
    display: 'flex',
    flexDirection: 'column',
    width: '100%',
    maxWidth: '480px',
    gap: '10px',
    marginBottom: '20px',
  },
  button: {
    padding: '12px 20px',
    fontSize: '16px',
    fontWeight: 'bold',
    color: 'white',
    backgroundColor: '#007bff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    width: '100%',
  },
  previewContainer: {
    marginTop: '20px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  sectionHeader: {
    color: '#333',
    marginBottom: '10px',
    textAlign: 'center',
  },
  previewImage: {
    maxWidth: '100%',
    borderRadius: '8px',
    border: '1px solid #ddd',
  },
  captionContainer: {
    marginTop: '20px',
    padding: '15px',
    backgroundColor: '#f8f9fa',
    borderRadius: '8px',
  },
  captionText: {
    fontSize: '18px',
    color: '#007bff',
    textAlign: 'center',
  },
  errorBox: {
    backgroundColor: '#ffeeee',
    padding: '10px',
    borderRadius: '5px',
    marginBottom: '15px',
  },
  errorText: {
    color: '#d32f2f',
    margin: 0,
  },
};

export default App;
