import React, { useState, useRef } from "react";
import axios from "axios";

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [caption, setCaption] = useState("");
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) return alert("Please select an image!");

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      const response = await axios.post("http://127.0.0.1:8000/caption/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setCaption(response.data.caption);
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
    } catch (error) {
      console.error("Error accessing camera:", error);
    }
  };

  const captureImage = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      setSelectedFile(new File([blob], "captured_image.jpg", { type: "image/jpeg" }));
    }, "image/jpeg");
  };

  return (
    <div className="container">
      <h2>VisionMate - Image Captioning</h2>

      {/* File Upload */}
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Generate Caption</button>

      {/* Camera Access */}
      <div>
        <video ref={videoRef} autoPlay width="400"></video>
        <canvas ref={canvasRef} width="400" height="300" style={{ display: "none" }}></canvas>
        <button onClick={startCamera}>Open Camera</button>
        <button onClick={captureImage}>Capture Image</button>
      </div>

      {/* Caption Display */}
      {caption && <p>Caption: {caption}</p>}
    </div>
  );
}

export default App;
