import React, { useState, useRef, useEffect } from "react";

function App() {
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [cameraPermission, setCameraPermission] = useState(null);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // Update this URL to your actual backend
  const BACKEND_URL = "http://192.168.1.108:8000/caption/";

  // Check if device is mobile
  const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);

  // Clean up function to stop camera
  const cleanupCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => {
        if (track.readyState === 'live') {
          track.stop();
        }
      });
      streamRef.current = null;
    }
  };

  // Cleanup on unmount
  useEffect(() => {
    return () => cleanupCamera();
  }, []);

  const playSound = (src) => {
    try {
      const audio = new Audio(src);
      audio.play().catch(() => {});
    } catch (error) {
      console.log("Audio play error:", error);
    }
  };

  const speak = (message) => {
    if ('speechSynthesis' in window) {
      try {
        const utterance = new SpeechSynthesisUtterance(message);
        speechSynthesis.speak(utterance);
      } catch (error) {
        console.log("Speech synthesis error:", error);
      }
    }
  };

  const generateCaption = async (blob) => {
    setLoading(true);
    setError(null);

    const formData = new FormData();
    const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
    formData.append("file", file);

    try {
      const response = await fetch(BACKEND_URL, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error(`Server error: ${response.status}`);

      const data = await response.json();
      setCaption(data.caption);
      speak(`Caption: ${data.caption}`);
    } catch (error) {
      setError("Failed to generate caption. Please try again.");
      speak("Error generating caption.");
      console.error("Upload error:", error);
    } finally {
      setLoading(false);
    }
  };

  // Check for camera permissions
  const checkCameraPermission = async () => {
    try {
      const result = await navigator.permissions.query({ name: 'camera' });
      setCameraPermission(result.state);
      return result.state;
    } catch (error) {
      console.log("Permission API not supported, will try direct access");
      return "prompt"; // Assume we need to prompt if the API isn't available
    }
  };

  const startCamera = async () => {
    // First, clean up any existing stream
    cleanupCamera();
    setError(null);

    try {
      // Check camera permission first
      await checkCameraPermission();

      const constraints = {
        video: isMobile 
          ? { facingMode: { exact: "environment" } } 
          : true,
        audio: false
      };

      console.log("Requesting camera with constraints:", JSON.stringify(constraints));

      // Force a small timeout to ensure DOM is ready
      await new Promise(resolve => setTimeout(resolve, 100));

      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      console.log("Camera stream obtained successfully");

      // Store the stream reference
      streamRef.current = stream;

      // Ensure video element is ready
      if (!videoRef.current) {
        console.error("Video element reference is null");
        setError("Camera initialization failed: video element not found");
        cleanupCamera();
        return;
      }

      // Set the stream as the video source
      videoRef.current.srcObject = stream;
      console.log("Stream assigned to video element");

      // Setup event handlers
      videoRef.current.onloadedmetadata = () => {
        console.log("Video metadata loaded, dimensions:", 
          videoRef.current.videoWidth, "x", videoRef.current.videoHeight);

        videoRef.current.play()
          .then(() => {
            console.log("Video playing successfully");
            setCameraActive(true);
          })
          .catch(err => {
            console.error("Video play error:", err);
            setError("Could not play video stream: " + err.message);
            cleanupCamera();
          });
      };

      videoRef.current.onerror = (event) => {
        console.error("Video element error:", event);
        setError("Video element error: " + (event.message || "Unknown error"));
        cleanupCamera();
      };

    } catch (error) {
      console.error("Camera access error:", error);

      // Provide more specific error messages
      if (error.name === "NotAllowedError") {
        setError("Camera access denied. Please grant camera permissions in your browser settings.");
      } else if (error.name === "NotFoundError") {
        setError("No camera found on this device.");
      } else if (error.name === "NotReadableError") {
        setError("Camera is in use by another application.");
      } else if (error.name === "OverconstrainedError") {
        // Try again with less strict constraints
        try {
          console.log("Retrying with basic constraints");
          const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: false });
          streamRef.current = stream;
          videoRef.current.srcObject = stream;
          videoRef.current.onloadedmetadata = () => {
            videoRef.current.play()
              .then(() => {
                setCameraActive(true);
              })
              .catch(e => {
                setError("Could not play video stream: " + e.message);
                cleanupCamera();
              });
          };
        } catch (retryError) {
          console.error("Retry failed:", retryError);
          setError("Camera not compatible with requested settings.");
          cleanupCamera();
        }
      } else {
        setError("Could not access camera: " + error.message);
      }
    }
  };

  const stopCamera = () => {
    cleanupCamera();
    setCameraActive(false);

    // Clear video source
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  const captureImage = () => {
    if (!videoRef.current) {
      console.error("Video ref not available");
      setError("Could not capture image: video element not found.");
      return;
    }

    if (!canvasRef.current) {
      console.error("Canvas ref not available");
      setError("Could not capture image: canvas element not found.");
      return;
    }

    try {
      const video = videoRef.current;
      const canvas = canvasRef.current;

      // Get the actual video dimensions
      const videoWidth = video.videoWidth;
      const videoHeight = video.videoHeight;

      console.log("Video dimensions for capture:", videoWidth, "x", videoHeight);

      if (videoWidth === 0 || videoHeight === 0) {
        console.error("Video dimensions are zero");
        setError("Cannot capture image: video stream not ready. Please restart the camera.");
        return;
      }

      // Set canvas dimensions to match video
      canvas.width = videoWidth;
      canvas.height = videoHeight;

      // Draw the video frame to the canvas
      const ctx = canvas.getContext('2d');
      ctx.drawImage(video, 0, 0, videoWidth, videoHeight);

      // Convert canvas to blob
      canvas.toBlob((blob) => {
        if (blob) {
          console.log("Image captured successfully, size:", blob.size, "bytes");
          const imageUrl = URL.createObjectURL(blob);
          setImagePreview(imageUrl);
          setCaption("");
          generateCaption(blob);
        } else {
          console.error("Failed to create blob from canvas");
          setError("Failed to process captured image.");
        }
      }, 'image/jpeg', 0.95);
    } catch (error) {
      console.error("Image capture error:", error);
      setError("Failed to capture image: " + error.message);
    }
  };

  // Handle keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === "c") startCamera();
      else if (event.key === "Escape") stopCamera();
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>üì∏ VisionMate</h1>

      {error && (
        <div style={styles.errorBox}>
          <p style={styles.errorText}>‚ö†Ô∏è {error}</p>
        </div>
      )}

      <div style={styles.cameraContainer}>
        {!cameraActive ? (
          <button 
            onClick={startCamera} 
            style={styles.button}
            disabled={loading}
          >
            üì∑ Open Camera
          </button>
        ) : (
          <>
            <div style={styles.videoContainer}>
              <video 
                ref={videoRef} 
                autoPlay 
                playsInline 
                muted
                style={styles.video} 
              />
            </div>

            <div style={styles.buttonContainer}>
              <button 
                onClick={captureImage} 
                style={styles.button}
                disabled={loading}
              >
                {loading ? "Processing..." : "üì∏ Capture Image"}
              </button>
              <button 
                onClick={stopCamera} 
                style={{...styles.button, backgroundColor: "#ff4d4d"}}
              >
                ‚ùå Close Camera
              </button>
            </div>
          </>
        )}
      </div>

      {/* Canvas for image processing - keep hidden but in the DOM */}
      <canvas ref={canvasRef} style={{ display: 'none' }} />

      {imagePreview && (
        <div style={styles.previewContainer}>
          <h3 style={styles.sectionHeader}>Image Preview</h3>
          <img 
            src={imagePreview} 
            alt="Captured" 
            style={styles.previewImage} 
          />
        </div>
      )}

      {caption && (
        <div style={styles.captionContainer}>
          <h3 style={styles.sectionHeader}>Caption</h3>
          <p style={styles.captionText}>{caption}</p>
        </div>
      )}

      <div style={styles.footer}>
        <p>Status: {cameraActive ? "Camera active" : "Camera inactive"}</p>
        {cameraPermission && <p>Camera permission: {cameraPermission}</p>}
      </div>
    </div>
  );
}

