import React, { useState, useRef } from "react";

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // Handle File Selection (Mobile + Laptop)
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(file));
    }
  };

  // Handle Upload to Backend
  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select or capture an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      const response = await fetch(`http://172.20.192.121:8000/caption/`, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Server responded with status: ${response.status}`);
      }

      const data = await response.json();
      setCaption(data.caption);

      // Speak out the caption (for accessibility)
      const utterance = new SpeechSynthesisUtterance(data.caption);
      speechSynthesis.speak(utterance);
    } catch (error) {
      alert("Error generating caption. Please try again.");
    }
  };

  // Start Camera (Laptop Webcam)
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      streamRef.current = stream;
      setCameraActive(true);

      setTimeout(() => {
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          videoRef.current.play();
        }
      }, 500);
    } catch (error) {
      alert("Could not access camera. Please check your permissions.");
    }
  };

  // Stop Camera
  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
    }
    setCameraActive(false);
  };

  // Capture Image from Webcam & Play Shutter Sound
  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) {
      return;
    }

    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(blob));

      // Play Camera Shutter Sound
      const audio = new Audio("publ");
      audio.play();
    }, "image/jpeg");
  };

  return (
    <div style={{ textAlign: "left", padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2>📸 VisionMate - AI Image Captioning</h2>

      {/* File Upload */}
      <label htmlFor="file-upload" style={{ fontWeight: "bold" }}>Select an Image:</label>
      <input id="file-upload" type="file" accept="image/*" capture="environment" onChange={handleFileChange} />
      <button onClick={handleUpload} style={buttonStyle}>📝 Generate Caption</button>

      {/* Camera Controls */}
      <div style={{ marginTop: "20px" }}>
        {!cameraActive ? (
          <button onClick={startCamera} style={buttonStyle}>📷 Open Camera</button>
        ) : (
          <>
            <video ref={videoRef} autoPlay width="400" height="300" style={{ borderRadius: "10px", border: "2px solid #000" }}></video>
            <canvas ref={canvasRef} width="400" height="300" style={{ display: "none" }}></canvas>
            <button onClick={captureImage} style={buttonStyle}>📸 Capture Image</button>
            <button onClick={stopCamera} style={{ ...buttonStyle, backgroundColor: "#ff4d4d" }}>❌ Close Camera</button>
          </>
        )}
      </div>

      {/* Image Preview */}
      {imagePreview && (
        <div style={{ marginTop: "20px" }}>
          <h4>📌 Preview:</h4>
          <img src={imagePreview} alt="Selected or Captured" width="300" style={{ borderRadius: "10px", border: "2px solid #000" }} />
        </div>
      )}

      {/* Caption Display */}
      {caption && (
        <p style={{ marginTop: "20px", fontSize: "18px", fontWeight: "bold", color: "#007bff" }}>
          📜 Caption: {caption}
        </p>
      )}
    </div>
  );
}

// Styling for buttons
const buttonStyle = {
  display: "block",
  margin: "10px 0",
  padding: "10px 15px",
  fontSize: "16px",
  fontWeight: "bold",
  backgroundColor: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  textAlign: "left",
};

export default App;
