import React, { useState, useRef } from "react";
import axios from "axios";

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [cameraActive, setCameraActive] = useState(false);

  // Handle File Upload
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    setImagePreview(URL.createObjectURL(file)); // Preview the uploaded image
  };

  // Handle Upload to Backend
  const handleUpload = async () => {
    if (!selectedFile) return alert("Please select or capture an image!");

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      const response = await axios.post("http://127.0.0.1:8000/caption/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setCaption(response.data.caption);
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  // Start Camera
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      setCameraActive(true);
    } catch (error) {
      console.error("Error accessing camera:", error);
    }
  };

  // Capture Image from Camera
  const captureImage = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(blob)); // Preview the captured image
    }, "image/jpeg");
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>VisionMate - Image Captioning</h2>

      {/* File Upload */}
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload} style={{ marginLeft: "10px" }}>Generate Caption</button>

      {/* Camera Capture Section */}
      <div style={{ marginTop: "20px" }}>
        {!cameraActive ? (
          <button onClick={startCamera}>Open Camera</button>
        ) : (
          <>
            <video ref={videoRef} autoPlay width="400"></video>
            <canvas ref={canvasRef} width="400" height="300" style={{ display: "none" }}></canvas>
            <button onClick={captureImage} style={{ marginLeft: "10px" }}>Capture Image</button>
          </>
        )}
      </div>

      {/* Display Image Preview */}
      {imagePreview && (
        <div style={{ marginTop: "20px" }}>
          <h4>Preview:</h4>
          <img src={imagePreview} alt="Selected or Captured" width="300" />
        </div>
      )}

      {/* Caption Display */}
      {caption && <p style={{ marginTop: "20px", fontSize: "18px" }}>Caption: {caption}</p>}
    </div>
  );
}

export default App;
