import React, { useState, useRef, useEffect } from "react";

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // Function to play sound cues
  const playSound = (src) => {
    const audio = new Audio(src);
    audio.play().catch((err) => console.log("Audio play blocked:", err));
  };

  // Function to speak text (Accessibility)
  const speak = (message) => {
    const utterance = new SpeechSynthesisUtterance(message);
    speechSynthesis.speak(utterance);
  };

  // Function to start listening for voice commands
  const startListening = () => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = false;
    recognition.lang = "en-US";
    recognition.start();

    recognition.onresult = (event) => {
      const command = event.results[0][0].transcript.toLowerCase();
      if (command.includes("capture")) {
        captureImage();
        speak("Image captured.");
      } else if (command.includes("generate")) {
        handleUpload();
        speak("Generating caption.");
      } else if (command.includes("exit")) {
        stopCamera();
        speak("Camera closed.");
      }
    };

    recognition.onerror = () => {
      speak("Sorry, I didn't understand that.");
    };
  };

  useEffect(() => {
    speak("Welcome to VisionMate. Select an image or open the camera to capture one.");
    startListening();
  }, []);

  // Handle File Selection
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(file));
      playSound("/click.mp3"); // Play click sound
      speak("Image selected.");
    }
  };

  // Handle Upload to Backend
  const handleUpload = async () => {
    if (!selectedFile) {
      speak("Please select or capture an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    speak("Generating caption. Please wait.");
    playSound("/beep.mp3"); // Play beep sound for processing

    try {
      const response = await fetch(`http://10.251.83.23:8000/caption/`, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Server responded with status: ${response.status}`);
      }

      const data = await response.json();
      setCaption(data.caption);
      speak(`Caption generated: ${data.caption}`);
      playSound("/ding.mp3"); // Play success sound
    } catch (error) {
      speak("Error generating caption. Please try again.");
    }
  };

  // Start Camera
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      streamRef.current = stream;
      setCameraActive(true);
      speak("Camera opened. Say 'capture' or press Capture Image.");
      setTimeout(() => {
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          videoRef.current.play();
        }
      }, 500);
    } catch (error) {
      speak("Could not access camera. Please check your permissions.");
    }
  };

  // Stop Camera
  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
    }
    setCameraActive(false);
    speak("Camera closed.");
  };

  // Capture Image & Play Shutter Sound
  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) {
      return;
    }

    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(blob));

      playSound("/shutter.mp3"); // Play camera shutter sound
      speak("Image captured.");
    }, "image/jpeg");
  };

  // Keyboard Shortcuts for Blind Navigation
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === "c") {
        startCamera();
      } else if (event.key === "Enter") {
        handleUpload();
      } else if (event.key === "Escape") {
        stopCamera();
      }
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div style={{ textAlign: "left", padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2>üì∏ VisionMate - AI Image Captioning</h2>

      {/* File Upload */}
      <label htmlFor="file-upload" style={{ fontWeight: "bold" }}>Select an Image:</label>
      <input id="file-upload" type="file" accept="image/*" capture="environment" onChange={handleFileChange} />
      <button onClick={handleUpload} style={buttonStyle}>üìù Generate Caption</button>

      {/* Camera Controls */}
      <div style={{ marginTop: "20px" }}>
        {!cameraActive ? (
          <button onClick={startCamera} style={buttonStyle}>üì∑ Open Camera</button>
        ) : (
          <>
            <video ref={videoRef} autoPlay width="400" height="300" style={{ borderRadius: "10px", border: "2px solid #000" }}></video>
            <canvas ref={canvasRef} width="400" height="300" style={{ display: "none" }}></canvas>
            <button onClick={captureImage} style={buttonStyle}>üì∏ Capture Image</button>
            <button onClick={stopCamera} style={{ ...buttonStyle, backgroundColor: "#ff4d4d" }}>‚ùå Close Camera</button>
          </>
        )}
      </div>

      {/* Image Preview */}
      {imagePreview && (
        <div style={{ marginTop: "20px" }}>
          <h4>üìå Preview:</h4>
          <img src={imagePreview} alt="Selected or Captured" width="300" style={{ borderRadius: "10px", border: "2px solid #000" }} />
        </div>
      )}

      {/* Caption Display */}
      {caption && (
        <p style={{ marginTop: "20px", fontSize: "18px", fontWeight: "bold", color: "#007bff" }}>
          üìú Caption: {caption}
        </p>
      )}
    </div>
  );
}

// Styling for buttons
const buttonStyle = {
  display: "block",
  margin: "10px 0",
  padding: "10px 15px",
  fontSize: "16px",
  fontWeight: "bold",
  backgroundColor: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  textAlign: "left",
};

export default App;
