import React, { useState, useRef, useEffect } from "react";

function App() {
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  const BACKEND_URL = "http://192.168.1.92:8000/caption/";

  const playSound = (src) => {
    const audio = new Audio(src);
    audio.play().catch((err) => console.log("Audio play blocked:", err));
  };

  const speak = (message) => {
    const utterance = new SpeechSynthesisUtterance(message);
    speechSynthesis.speak(utterance);
  };

  useEffect(() => {
    speak("Welcome to VisionMate. Tap Open Camera or press C to start.");
    return () => stopCamera();
  }, []);

  const generateCaption = async (blob) => {
    const formData = new FormData();
    const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
    formData.append("file", file);

    speak("Generating caption. Please wait.");
    playSound("/beep.mp3");

    try {
      const response = await fetch(BACKEND_URL, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error(`Server error: ${response.status}`);
      const data = await response.json();
      setCaption(data.caption);
      speak(`Caption: ${data.caption}`);
      playSound("/ding.mp3");
    } catch (error) {
      speak("Error generating caption. Please try again.");
      console.error("Upload error:", error.message);
    }
  };

  const startCamera = async () => {
    try {
      // Check permission status
      if (navigator.permissions) {
        navigator.permissions.query({ name: "camera" }).then((result) => {
          console.log("Camera permission status:", result.state);
        });
      }

      const constraints = {
        video: {
          facingMode: "environment",
          width: { ideal: 1280 },
          height: { ideal: 720 },
        },
      };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;
      setCameraActive(true);
      speak("Camera opened. Tap Capture to take a picture.");

      setTimeout(() => {
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          videoRef.current.play();
        }
      }, 300);
    } catch (error) {
      console.error("Camera error:", error);
      speak("Could not access the camera. Please check browser permissions.");
    }
  };


  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
    }
    setCameraActive(false);
    speak("Camera closed.");
  };

  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");

    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;

    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      setImagePreview(URL.createObjectURL(blob));
      setCaption("");
      playSound("/shutter.mp3");
      speak("Image captured. Generating caption now.");
      generateCaption(blob);
    }, "image/jpeg");
  };

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === "c") startCamera();
      else if (event.key === "Escape") stopCamera();
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div style={wrapperStyle}>
      <h1 style={titleStyle}>📸 VisionMate</h1>
      <p style={subtitleStyle}>AI Image Captioning for Everyone</p>

      <div style={cardStyle}>
        {!cameraActive ? (
          <button onClick={startCamera} style={buttonStyle}>📷 Open Camera</button>
        ) : (
          <>
            <video ref={videoRef} autoPlay playsInline muted style={videoStyle} />
            <canvas ref={canvasRef} style={{ display: "none" }} />
            <button onClick={captureImage} style={buttonStyle}>📸 Capture Image</button>
            <button onClick={stopCamera} style={{ ...buttonStyle, backgroundColor: "#ff4d4d" }}>
              ❌ Close Camera
            </button>
          </>
        )}

        {imagePreview && (
          <div style={{ marginTop: "20px" }}>
            <h4 style={subHeaderStyle}>📌 Preview:</h4>
            <img src={imagePreview} alt="Captured" style={imageStyle} />
          </div>
        )}

        {caption && (
          <p style={captionStyle}>
            📜 Caption: {caption}
          </p>
        )}
      </div>

      <footer style={footerStyle}>
        Built by Anoushka <span style={{ color: "hotpink" }}>❤</span>
      </footer>
    </div>
  );
}

// Styles
const wrapperStyle = {
  fontFamily: "Arial, sans-serif",
  padding: "20px",
  backgroundColor: "#f8f9fa",
  minHeight: "100vh",
  textAlign: "center",
};

const cardStyle = {
  backgroundColor: "#fff",
  padding: "20px",
  borderRadius: "12px",
  boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
  maxWidth: "500px",
  margin: "auto",
};

const titleStyle = {
  fontSize: "2.5rem",
  fontWeight: "bold",
  marginBottom: "5px",
};

const subtitleStyle = {
  fontSize: "1.1rem",
  color: "#555",
  marginBottom: "20px",
};

const buttonStyle = {
  display: "block",
  width: "100%",
  margin: "10px 0",
  padding: "12px 15px",
  fontSize: "1rem",
  fontWeight: "bold",
  backgroundColor: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "6px",
  cursor: "pointer",
  touchAction: "manipulation",
};

const subHeaderStyle = {
  fontSize: "1.1rem",
  marginBottom: "10px",
};

const videoStyle = {
  width: "100%",
  aspectRatio: "4/3",
  borderRadius: "10px",
  border: "2px solid #000",
  marginBottom: "10px",
  objectFit: "cover",
};

const imageStyle = {
  width: "100%",
  borderRadius: "10px",
  border: "2px solid #000",
};

const captionStyle = {
  marginTop: "20px",
  fontSize: "1.1rem",
  fontWeight: "bold",
  color: "#007bff",
  wordWrap: "break-word",
};

const footerStyle = {
  marginTop: "40px",
  fontSize: "1rem",
  color: "#666",
};

export default App;
