import React, { useState, useRef, useEffect } from "react";

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // 🔊 Sound cue helper
  const playSound = (src) => {
    const audio = new Audio(src);
    audio.play().catch((err) => console.log("Audio play blocked:", err));
  };

  // 📣 Text-to-speech
  const speak = (message) => {
    const utterance = new SpeechSynthesisUtterance(message);
    speechSynthesis.speak(utterance);
  };

  // 🎙️ Voice Commands
  const startListening = () => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = false;
    recognition.lang = "en-US";
    recognition.start();

    recognition.onresult = (event) => {
      const command = event.results[0][0].transcript.toLowerCase();
      if (command.includes("capture")) {
        captureImage();
        speak("Image captured.");
      } else if (command.includes("generate")) {
        handleUpload();
        speak("Generating caption.");
      } else if (command.includes("exit")) {
        stopCamera();
        speak("Camera closed.");
      }
    };

    recognition.onerror = () => {
      speak("Sorry, I didn't understand that.");
    };
  };

  useEffect(() => {
    speak("Welcome to VisionMate. Press C to open camera, Enter to generate caption, or say a command.");
    startListening();
  }, []);

  // 📤 Send image to backend
  const handleUpload = async () => {
    if (!selectedFile) {
      speak("Please capture an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    speak("Generating caption. Please wait.");
    playSound("/beep.mp3");

    try {
      const response = await fetch("http://192.168.1.108:8000/caption/", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }

      const data = await response.json();
      setCaption(data.caption);
      speak(`Caption: ${data.caption}`);
      playSound("/ding.mp3");
    } catch (error) {
      speak("Error generating caption. Please try again.");
    }
  };

  // 📷 Open camera
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      streamRef.current = stream;
      setCameraActive(true);
      speak("Camera opened. Say capture to take a photo.");
      setTimeout(() => {
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          videoRef.current.play();
        }
      }, 500);
    } catch (error) {
      speak("Could not access the camera.");
    }
  };

  // ❌ Close camera
  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
    }
    setCameraActive(false);
    speak("Camera closed.");
  };

  // 📸 Capture photo
  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(blob));
      setCaption("");

      playSound("/shutter.mp3");
      speak("Image captured.");
    }, "image/jpeg");
  };

  // ⌨️ Keyboard accessibility
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === "c") {
        startCamera();
      } else if (event.key === "Enter") {
        handleUpload();
      } else if (event.key === "Escape") {
        stopCamera();
      }
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div style={{ textAlign: "left", padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2>📸 VisionMate - AI Image Captioning</h2>

      {/* 🎥 Camera Section */}
      {!cameraActive ? (
        <button onClick={startCamera} style={buttonStyle}>📷 Open Camera</button>
      ) : (
        <>
          <video ref={videoRef} autoPlay width="400" height="300" style={videoStyle}></video>
          <canvas ref={canvasRef} width="400" height="300" style={{ display: "none" }}></canvas>
          <button onClick={captureImage} style={buttonStyle}>📸 Capture Image</button>
          <button onClick={stopCamera} style={{ ...buttonStyle, backgroundColor: "#ff4d4d" }}>❌ Close Camera</button>
        </>
      )}

      {/* 🖼️ Image Preview */}
      {imagePreview && (
        <div style={{ marginTop: "20px" }}>
          <h4>📌 Preview:</h4>
          <img src={imagePreview} alt="Captured" width="300" style={imageStyle} />

          {/* 🧠 Generate Caption Button */}
          <button onClick={handleUpload} style={buttonStyle}>📝 Generate Caption</button>
        </div>
      )}

      {/* 📜 Caption */}
      {caption && (
        <p style={{ marginTop: "20px", fontSize: "18px", fontWeight: "bold", color: "#007bff" }}>
          📜 Caption: {caption}
        </p>
      )}

      {/* 🎙️ Restart Voice Assistant */}
      <button onClick={startListening} style={buttonStyle}>🎤 Restart Voice Assistant</button>
    </div>
  );
}

// 🔘 Styling
const buttonStyle = {
  display: "block",
  margin: "10px 0",
  padding: "10px 15px",
  fontSize: "16px",
  fontWeight: "bold",
  backgroundColor: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  textAlign: "left",
};

const videoStyle = {
  borderRadius: "10px",
  border: "2px solid #000",
  marginBottom: "10px",
};

const imageStyle = {
  borderRadius: "10px",
  border: "2px solid #000",
};

export default App;
