import React, { useState, useRef, useEffect } from "react";

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);
  const recognitionRef = useRef(null);

  // 🎧 Play sound cues
  const playSound = (src) => {
    const audio = new Audio(src);
    audio.play().catch((err) => console.log("Audio play blocked:", err));
  };

  // 🗣️ Text-to-speech feedback
  const speak = (message) => {
    const utterance = new SpeechSynthesisUtterance(message);
    speechSynthesis.speak(utterance);
  };

  // 🎙️ Initialize continuous voice recognition
  const initVoiceRecognition = async () => {
    if (!("webkitSpeechRecognition" in window)) {
      speak("Speech recognition is not supported in this browser.");
      return;
    }

    // Request microphone permission first
    try {
      await navigator.mediaDevices.getUserMedia({ audio: true });
      console.log("Microphone access granted!");
    } catch (err) {
      speak("Microphone access denied. Please allow microphone permissions in your browser.");
      console.error("Microphone permission denied:", err);
      return;
    }

    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.lang = "en-US";
    recognition.interimResults = false;

    recognition.onresult = (event) => {
      const command = event.results[event.resultIndex][0].transcript.toLowerCase().trim();
      console.log("🎤 Command heard:", command);

      if (command.includes("capture")) {
        captureImage();
        speak("Image captured.");
      } else if (command.includes("generate")) {
        handleUpload();
        speak("Generating caption.");
      } else if (command.includes("exit")) {
        stopCamera();
        speak("Camera closed.");
      } else {
        speak("Sorry, I didn't understand that.");
      }
    };

    recognition.onerror = (e) => {
      console.error("Voice recognition error:", e.error);
      if (e.error === "no-speech") {
        recognition.start(); // Restart if no speech detected
      } else if (e.error === "not-allowed" || e.error === "service-not-allowed") {
        speak("Microphone access denied. Please check permissions.");
      }
    };

    recognition.onend = () => {
      if (recognitionRef.current) {
        recognition.start(); // Auto-restart unless explicitly stopped
      }
    };

    recognition.start();
    recognitionRef.current = recognition;
  };

  // Start voice recognition and welcome message on mount
  useEffect(() => {
    speak("Welcome to VisionMate. Say 'capture', 'generate', or 'exit'.");
    initVoiceRecognition();

    // Cleanup on unmount
    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
        recognitionRef.current = null;
      }
      stopCamera();
    };
  }, []);

  // 📤 Upload image to backend
  const handleUpload = async () => {
    if (!selectedFile) {
      speak("Please capture an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    speak("Generating caption. Please wait.");
    playSound("/beep.mp3");

    try {
      const response = await fetch("http://192.168.1.108:8000/caption/", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error(`Server error: ${response.status}`);
      const data = await response.json();
      setCaption(data.caption);
      speak(`Caption: ${data.caption}`);
      playSound("/ding.mp3");
    } catch (error) {
      speak("Error generating caption. Please try again.");
      console.error("Upload error:", error);
    }
  };

  // 🎥 Open webcam
  const startCamera = async () => {
    try {
      const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);
      const constraints = {
        video: isMobile ? { facingMode: { ideal: "environment" } } : true,
      };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;
      setCameraActive(true);

      speak("Camera opened. Say 'capture' or press the button.");

      setTimeout(() => {
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          videoRef.current.play();
        }
      }, 500);
    } catch (error) {
      console.error("Camera error:", error);
      speak("Could not access the camera. Please check browser permissions.");
    }
  };

  // ❌ Stop webcam
  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
    }
    setCameraActive(false);
    speak("Camera closed.");
  };

  // 📸 Capture image from webcam
  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(blob));
      setCaption("");

      playSound("/shutter.mp3");
    }, "image/jpeg");
  };

  // ⌨️ Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === "c") startCamera();
      else if (event.key === "Enter") handleUpload();
      else if (event.key === "Escape") stopCamera();
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div style={{ textAlign: "left", padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2>📸 VisionMate - AI Image Captioning</h2>

      {/* Camera Controls */}
      {!cameraActive ? (
        <button onClick={startCamera} style={buttonStyle}>📷 Open Camera</button>
      ) : (
        <>
          <video ref={videoRef} autoPlay width="400" height="300" style={videoStyle}></video>
          <canvas ref={canvasRef} width="400" height="300" style={{ display: "none" }}></canvas>
          <button onClick={captureImage} style={buttonStyle}>📸 Capture Image</button>
          <button onClick={stopCamera} style={{ ...buttonStyle, backgroundColor: "#ff4d4d" }}>
            ❌ Close Camera
          </button>
        </>
      )}

      {/* Image Preview & Captioning */}
      {imagePreview && (
        <div style={{ marginTop: "20px" }}>
          <h4>📌 Preview:</h4>
          <img src={imagePreview} alt="Captured" width="300" style={imageStyle} />
          <button onClick={handleUpload} style={buttonStyle}>📝 Generate Caption</button>
        </div>
      )}

      {/* Caption Output */}
      {caption && (
        <p style={{ marginTop: "20px", fontSize: "18px", fontWeight: "bold", color: "#007bff" }}>
          📜 Caption: {caption}
        </p>
      )}

      {/* Manual Restart for Voice Assistant */}
      <button onClick={initVoiceRecognition} style={buttonStyle}>🎤 Restart Voice Assistant</button>
    </div>
  );
}

// Styles
const buttonStyle = {
  display: "block",
  margin: "10px 0",
  padding: "10px 15px",
  fontSize: "16px",
  fontWeight: "bold",
  backgroundColor: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  textAlign: "left",
};

const videoStyle = {
  borderRadius: "10px",
  border: "2px solid #000",
  marginBottom: "10px",
};

const imageStyle = {
  borderRadius: "10px",
  border: "2px solid #000",
};

export default App;
