const wrapperStyle = {
  fontFamily: "Arial, sans-serif",
  padding: "20px",
  backgroundColor: "#f8f9fa",
  minHeight: "100vh",
  textAlign: "center",
};

const cardStyle = {
  backgroundColor: "#fff",
  padding: "20px",
  borderRadius: "12px",
  boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
  maxWidth: "500px",
  margin: "auto",
};

const titleStyle = {
  fontSize: "2.5rem",
  fontWeight: "bold",
  marginBottom: "5px",
};

const subtitleStyle = {
  fontSize: "1.1rem",
  color: "#555",
  marginBottom: "20px",
};

const buttonStyle = {
  display: "block",
  width: "100%",
  margin: "10px 0",
  padding: "12px 15px",
  fontSize: "1rem",
  fontWeight: "bold",
  backgroundColor: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "6px",
  cursor: "pointer",
  touchAction: "manipulation",
};

const subHeaderStyle = {
  fontSize: "1.1rem",
  marginBottom: "10px",
};

const videoStyle = {
  width: "100%",
  aspectRatio: "4/3",
  borderRadius: "10px",
  border: "2px solid #000",
  marginBottom: "10px",
  objectFit: "cover",
};

const imageStyle = {
  width: "100%",
  borderRadius: "10px",
  border: "2px solid #000",
};

const captionStyle = {
  marginTop: "20px",
  fontSize: "1.1rem",
  fontWeight: "bold",
  color: "#007bff",
  wordWrap: "break-word",
};

const footerStyle = {
  marginTop: "40px",
  fontSize: "1rem",
  color: "#666",
};
