import React, { useState, useRef } from "react";

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);
  const audioRef = useRef(new Audio("/shutter.mp3")); // Shutter sound

  // Text-to-Speech Function
  const speakText = (text) => {
    const synth = window.speechSynthesis;
    const utterance = new SpeechSynthesisUtterance(text);
    synth.speak(utterance);
  };

  // Handle File Selection (Mobile + Laptop)
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(file));
    }
  };

  // Handle Upload to Backend
  const backendURL = `http://${window.location.hostname}:8000/caption/`;

const handleUpload = async () => {
    if (!selectedFile) {
        console.error("No file selected");
        alert("Please select or capture an image first.");
        return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    console.log("Sending request to backend at:", backendURL);

    try {
        const response = await fetch(backendURL, {
            method: "POST",
            body: formData,
        });

        if (!response.ok) {
            throw new Error(`Server responded with status: ${response.status}`);
        }

        const data = await response.json();
        console.log("Caption received:", data.caption);
        setCaption(data.caption);
    } catch (error) {
        console.error("Error fetching caption:", error);
        alert("Error generating caption. Please try again.");
    }
};


  // Start Camera (Laptop Webcam)
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      streamRef.current = stream;
      setCameraActive(true);
      setTimeout(() => {
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          videoRef.current.play();
        }
      }, 500);
    } catch (error) {
      alert("Could not access camera. Check permissions.");
    }
  };

  // Stop Camera
  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
    }
    setCameraActive(false);
  };

  // Capture Image from Webcam
  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) {
      alert("Camera is not active.");
      return;
    }

    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(blob));
      audioRef.current.play(); // Play shutter sound
    }, "image/jpeg");
  };

  return (
    <div style={{ textAlign: "center", padding: "20px", fontSize: "18px" }}>
      <h2 style={{ fontSize: "28px" }}>üñºÔ∏è VisionMate - AI Image Captioning</h2>

      {/* Mobile: File Upload & Open Camera */}
      <label htmlFor="fileUpload" style={{ fontSize: "20px", fontWeight: "bold" }}>Select an Image:</label>
      <input type="file" id="fileUpload" accept="image/*" capture="environment" onChange={handleFileChange} style={{ fontSize: "18px" }} />

      <button onClick={handleUpload} style={{ fontSize: "18px", marginLeft: "10px", padding: "10px" }}>üìú Generate Caption</button>

      {/* Laptop: Webcam Access */}
      <div style={{ marginTop: "20px" }}>
        {!cameraActive ? (
          <button onClick={startCamera} style={{ fontSize: "18px", padding: "10px" }}>üì∑ Open Camera</button>
        ) : (
          <>
            <video ref={videoRef} autoPlay width="400" height="300" style={{ marginTop: "20px" }}></video>
            <canvas ref={canvasRef} width="400" height="300" style={{ display: "none" }}></canvas>
            <button onClick={captureImage} style={{ margin: "10px", padding: "10px", fontSize: "18px" }}>üì∏ Capture Image</button>
            <button onClick={stopCamera} style={{ padding: "10px", fontSize: "18px" }}>‚ùå Close Camera</button>
          </>
        )}
      </div>

      {/* Display Image Preview */}
      {imagePreview && (
        <div style={{ marginTop: "20px" }}>
          <h3>üìå Preview:</h3>
          <img src={imagePreview} alt="Selected or Captured" width="300" />
        </div>
      )}

      {/* Caption Display */}
      {caption && (
        <div style={{ marginTop: "20px" }}>
          <h3>üìù Caption:</h3>
          <p style={{ fontSize: "22px", fontWeight: "bold" }}>{caption}</p>
          <button onClick={() => speakText(caption)} style={{ fontSize: "18px", padding: "10px" }}>üîä Speak Caption</button>
        </div>
      )}
    </div>
  );
}

export default App;
