import React, { useState, useRef, useEffect } from "react";

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // üéµ Play sound helper
  const playSound = (src) => {
    const audio = new Audio(src);
    audio.play().catch((err) => console.log("Audio play blocked:", err));
  };

  // üó£ Speak text aloud
  const speak = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    speechSynthesis.speak(utterance);
  };

  // üé§ Voice assistant commands
  const startListening = () => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = false;
    recognition.lang = "en-US";
    recognition.start();

    recognition.onresult = (event) => {
      const command = event.results[0][0].transcript.toLowerCase();
      if (command.includes("capture")) {
        captureImage();
        speak("Image captured.");
      } else if (command.includes("generate")) {
        handleUpload();
        speak("Generating caption.");
      } else if (command.includes("exit") || command.includes("close")) {
        stopCamera();
        speak("Camera closed.");
      }
    };

    recognition.onerror = () => {
      speak("Sorry, I didn't understand that.");
    };
  };

  // üîä Welcome message on load
  useEffect(() => {
    speak("Welcome to VisionMate. You can upload an image or open the camera. Say capture or press C to take a photo.");
    startListening();
  }, []);

  // üìÅ Handle file selection
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(file));
      playSound("/click.mp3");
      speak("Image selected.");
    }
  };

  // üì° Send to backend
  const handleUpload = async () => {
    if (!selectedFile) {
      speak("Please select or capture an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    speak("Generating caption. Please wait.");
    playSound("/beep.mp3");

    try {
      const response = await fetch(`http://192.168.1.108:8000/caption/`, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Server responded with status: ${response.status}`);
      }

      const data = await response.json();
      setCaption(data.caption);
      speak(`Caption generated: ${data.caption}`);
      playSound("/ding.mp3");
    } catch (error) {
      speak("Error generating caption. Please try again.");
    }
  };

  // üé• Start webcam
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      streamRef.current = stream;
      setCameraActive(true);
      speak("Camera opened. Say capture or press C to take photo.");
      setTimeout(() => {
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          videoRef.current.play();
        }
      }, 500);
    } catch (error) {
      speak("Could not access camera. Please check your permissions.");
    }
  };

  // ‚ùå Stop webcam
  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
    }
    setCameraActive(false);
    speak("Camera closed.");
  };

  // üì∏ Capture photo from webcam
  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
      setSelectedFile(file);
      setImagePreview(URL.createObjectURL(blob));
      playSound("/shutter.mp3");
      speak("Image captured.");
    }, "image/jpeg");
  };

  // ‚å®Ô∏è Keyboard accessibility
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === "c") startCamera();
      else if (e.key === "Enter") handleUpload();
      else if (e.key === "Escape") stopCamera();
    };
    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div style={containerStyle}>
      <h1 style={titleStyle}>üëÅÔ∏è‚Äçüó®Ô∏è Welcome to VisionMate</h1>
      <h2 style={subtitleStyle}>AI Image Captioning for the Visually Impaired</h2>

      <label htmlFor="file-upload" style={{ fontWeight: "bold" }}>Select an Image:</label>
      <input id="file-upload" type="file" accept="image/*" capture="environment" onChange={handleFileChange} />

      <button onClick={handleUpload} style={buttonStyle}>üìù Generate Caption</button>

      <div style={{ marginTop: "20px" }}>
        {!cameraActive ? (
          <button onClick={startCamera} style={buttonStyle}>üì∑ Open Camera</button>
        ) : (
          <>
            <video ref={videoRef} autoPlay width="100%" height="300" style={videoStyle}></video>
            <canvas ref={canvasRef} width="400" height="300" style={{ display: "none" }}></canvas>
            <button onClick={captureImage} style={buttonStyle}>üì∏ Capture Image</button>
            <button onClick={stopCamera} style={dangerButtonStyle}>‚ùå Close Camera</button>
          </>
        )}
      </div>

      {imagePreview && (
        <div style={{ marginTop: "20px" }}>
          <h4>üìå Preview:</h4>
          <img src={imagePreview} alt="Selected or Captured" width="100%" style={imageStyle} />
        </div>
      )}

      {caption && (
        <p style={captionStyle}>
          üìú Caption: {caption}
        </p>
      )}

      {/* Voice Assistant Restart Button */}
      <button onClick={startListening} style={secondaryButtonStyle}>üé§ Restart Voice Assistant</button>

      <p style={helperStyle}>
        üí° Voice commands and keyboard shortcuts supported. Say "capture", "generate", or "exit". <br />
        Press C = Open Camera | Enter = Generate Caption | Esc = Close Camera
      </p>

      <footer style={footerStyle}>
        Built with üíô for accessibility by Anoushka
      </footer>
    </div>
  );
}

const containerStyle = {
  backgroundColor: "#f9f9f9",
  padding: "30px",
  borderRadius: "15px",
  boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
  maxWidth: "600px",
  margin: "40px auto",
  fontFamily: "'Segoe UI', sans-serif",
  textAlign: "left",
};

const titleStyle = {
  fontSize: "28px",
  fontWeight: "bold",
  marginBottom: "10px",
  textAlign: "center",
  color: "#333",
};

const subtitleStyle = {
  fontSize: "18px",
  textAlign: "center",
  marginBottom: "25px",
  color: "#666",
};

const buttonStyle = {
  display: "block",
  margin: "10px 0",
  padding: "10px 15px",
  fontSize: "16px",
  fontWeight: "bold",
  backgroundColor: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  textAlign: "left",
};

const dangerButtonStyle = {
  ...buttonStyle,
  backgroundColor: "#ff4d4d",
};

const secondaryButtonStyle = {
  ...buttonStyle,
  backgroundColor: "#6c757d",
};

const imageStyle = {
  borderRadius: "10px",
  border: "2px solid #000",
  marginTop: "10px",
};

const videoStyle = {
  borderRadius: "10px",
  border: "2px solid #000",
  marginBottom: "10px",
};

const captionStyle = {
  marginTop: "20px",
  fontSize: "18px",
  fontWeight: "bold",
  color: "#007bff",
};

const helperStyle = {
  fontSize: "14px",
  color: "#555",
  marginTop: "20px",
};

const footerStyle = {
  marginTop: "40px",
  fontSize: "14px",
  textAlign: "center",
  color: "#888",
};

export default App;
