import React, { useState, useRef, useEffect } from "react";

function App() {
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isMobile, setIsMobile] = useState(false);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // Update this URL to your actual backend
  const BACKEND_URL = "http://192.168.1.108:8000/caption/";

  // Check if device is mobile on component mount
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(/iPhone|iPad|iPod|Android/i.test(navigator.userAgent));
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    return () => {
      window.removeEventListener('resize', checkMobile);
      stopCamera();
    };
  }, []);

  // Welcome message on mount
  useEffect(() => {
    speak("Welcome to VisionMate. Press the camera button to take a picture.");
  }, []);

  const playSound = (src) => {
    const audio = new Audio(src);
    audio.play().catch((err) => console.log("Audio play blocked:", err));
  };

  const speak = (message) => {
    // Check if speech synthesis is available
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(message);
      speechSynthesis.speak(utterance);
    }
  };

  const generateCaption = async (blob) => {
    setLoading(true);
    setError(null);

    const formData = new FormData();
    const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
    formData.append("file", file);

    speak("Generating caption. Please wait.");
    playSound("/beep.mp3");

    try {
      const response = await fetch(BACKEND_URL, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error(`Server error: ${response.status}`);

      const data = await response.json();
      setCaption(data.caption);
      speak(`Caption: ${data.caption}`);
      playSound("/ding.mp3");
    } catch (error) {
      setError("Failed to generate caption. Please try again.");
      speak("Error generating caption. Please try again.");
      console.error("Upload error:", error.message);
    } finally {
      setLoading(false);
    }
  };

  const startCamera = async () => {
    try {
      // Get the device's screen dimensions
      const screenWidth = window.innerWidth;
      const screenHeight = window.innerHeight;

      // Configure video constraints based on device type
      const constraints = {
        video: {
          facingMode: isMobile ? "environment" : "user",
          width: { ideal: Math.min(screenWidth, 640) },
          height: { ideal: Math.min(screenHeight * 0.6, 480) }
        },
        audio: false
      };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;

      if (videoRef.current) {
        videoRef.current.srcObject = stream;

        // Wait for video metadata to load before playing
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play().catch(err => {
            console.error("Video play error:", err);
            speak("Could not start camera preview. Please check permissions.");
          });
        };
      }

      setCameraActive(true);
      speak("Camera opened. Tap Capture to take a picture.");
    } catch (error) {
      console.error("Camera error:", error);
      setError("Could not access camera. Please check browser permissions and try again.");
      speak("Could not access the camera. Please check browser permissions.");
    }
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
    }
    setCameraActive(false);
    speak("Camera closed.");
  };

  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;

    // Set canvas dimensions to match the video
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    const context = canvas.getContext("2d");
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      if (blob) {
        setImagePreview(URL.createObjectURL(blob));
        setCaption("");
        playSound("/shutter.mp3");
        speak("Image captured. Generating caption now.");
        generateCaption(blob);
      } else {
        setError("Failed to capture image. Please try again.");
        speak("Failed to capture image. Please try again.");
      }
    }, "image/jpeg", 0.8);
  };

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === "c") startCamera();
      else if (event.key === "Escape") stopCamera();
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div style={containerStyle(isMobile)}>
      <h2 style={headerStyle}>📸 VisionMate</h2>

      {error && (
        <div style={errorStyle}>
          ⚠️ {error}
        </div>
      )}

      {!cameraActive ? (
        <button onClick={startCamera} style={buttonStyle}>
          📷 Open Camera
        </button>
      ) : (
        <div style={cameraContainerStyle(isMobile)}>
          <video 
            ref={videoRef} 
            playsInline 
            autoPlay 
            style={videoStyle(isMobile)} 
          />
          <canvas ref={canvasRef} style={{ display: "none" }} />

          <div style={cameraControlsStyle}>
            <button onClick={captureImage} style={buttonStyle} disabled={loading}>
              {loading ? "Processing..." : "📸 Capture"}
            </button>
            <button onClick={stopCamera} style={{ ...buttonStyle, backgroundColor: "#ff4d4d" }}>
              ❌ Close Camera
            </button>
          </div>
        </div>
      )}

      {imagePreview && (
        <div style={previewContainerStyle}>
          <h4 style={subHeaderStyle}>📌 Preview:</h4>
          <img 
            src={imagePreview} 
            alt="Captured" 
            style={imageStyle(isMobile)} 
          />
        </div>
      )}

      {caption && (
        <div style={captionContainerStyle}>
          <h4 style={subHeaderStyle}>📜 Caption:</h4>
          <p style={captionTextStyle}>{caption}</p>
        </div>
      )}

      <div style={instructionsStyle}>
        <p>Press C key to open camera (desktop only)</p>
        <p>Press ESC key to close camera (desktop only)</p>
      </div>
    </div>
  );
}

// Responsive style functions
const containerStyle = (isMobile) => ({
  textAlign: "center",
  padding: isMobile ? "10px" : "20px",
  maxWidth: "100%",
  margin: "auto",
  fontFamily: "Arial, sans-serif",
});

const headerStyle = {
  margin: "10px 0 20px 0",
  color: "#007bff",
};

const subHeaderStyle = {
  marginTop: "10px",
  marginBottom: "5px",
};

const buttonStyle = {
  display: "block",
  width: "100%",
  margin: "10px 0",
  padding: "12px 15px",
  fontSize: "16px",
  fontWeight: "bold",
  backgroundColor: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  textAlign: "center",
};

const cameraContainerStyle = (isMobile) => ({
  width: "100%",
  marginBottom: "15px",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
});

const videoStyle = (isMobile) => ({
  width: "100%",
  maxWidth: "400px",
  borderRadius: "10px",
  border: "2px solid #000",
  marginBottom: "10px",
  transform: isMobile ? "scaleX(1)" : "scaleX(1)", // Flip horizontally for front camera if needed
});

const imageStyle = (isMobile) => ({
  width: "100%",
  maxWidth: "400px",
  borderRadius: "10px",
  border: "2px solid #000",
});

const cameraControlsStyle = {
  width: "100%",
  maxWidth: "400px",
};

const previewContainerStyle = {
  marginTop: "20px",
  width: "100%",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
};

const captionContainerStyle = {
  marginTop: "10px",
  padding: "10px",
  backgroundColor: "#f8f9fa",
  borderRadius: "5px",
  width: "100%",
  maxWidth: "400px",
  margin: "10px auto",
};

const captionTextStyle = {
  fontSize: "18px",
  fontWeight: "bold",
  color: "#007bff",
  margin: "5px 0",
  wordWrap: "break-word",
};

const errorStyle = {
  backgroundColor: "#ffeeee",
  color: "#d32f2f",
  padding: "10px",
  borderRadius: "5px",
  marginBottom: "15px",
};

const instructionsStyle = {
  fontSize: "12px",
  color: "#666",
  marginTop: "20px",
  display: "flex",
  flexDirection: "column",
  gap: "5px",
};

export default App;
