import React, { useState, useRef, useEffect } from "react";

function App() {
  const [caption, setCaption] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const [errorMessage, setErrorMessage] = useState(""); // Added for debugging
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // Use HTTPS or localhost for testing
  const BACKEND_URL = "http://192.168.1.108:8000/caption/"; // Replace with HTTPS if not localhost

  const playSound = (src) => {
    const audio = new Audio(src);
    audio.play().catch((err) => console.log("Audio play blocked:", err));
  };

  const speak = (message) => {
    const utterance = new SpeechSynthesisUtterance(message);
    speechSynthesis.speak(utterance);
  };

  useEffect(() => {
    speak("Welcome to VisionMate. Tap Open Camera or press C to start.");
    return () => stopCamera();
  }, []);

  const generateCaption = async (blob) => {
    const formData = new FormData();
    const file = new File([blob], "captured_image.jpg", { type: "image/jpeg" });
    formData.append("file", file);

    speak("Generating caption. Please wait.");
    playSound("/beep.mp3");

    try {
      const response = await fetch(BACKEND_URL, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error(`Server error: ${response.status}`);
      const data = await response.json();
      setCaption(data.caption);
      speak(`Caption: ${data.caption}`);
      playSound("/ding.mp3");
    } catch (error) {
      speak("Error generating caption. Please try again.");
      console.error("Upload error:", error.message);
    }
  };

  const startCamera = async () => {
    try {
      // Simplified constraints for broader compatibility
      const constraints = {
        video: {
          facingMode: "environment", // Rear camera
        },
      };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;
      setCameraActive(true);
      setErrorMessage(""); // Clear any previous errors
      speak("Camera opened. Tap Capture to take a picture.");

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.play().catch((err) => {
          console.error("Video play error:", err);
          setErrorMessage("Failed to play video stream.");
          speak("Error playing camera feed.");
        });
      }
    } catch (error) {
      console.error("Camera access error:", error.name, error.message);
      setErrorMessage(`Camera error: ${error.name} - ${error.message}`);
      let message = "Could not access the camera.";
      if (error.name === "NotAllowedError") {
        message += " Permission denied. Please allow camera access in settings.";
      } else if (error.name === "NotFoundError") {
        message += " No camera found on this device.";
      } else {
        message += " Check browser permissions or try again.";
      }
      speak(message);
    }
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
    }
    setCameraActive(false);
    speak("Camera closed.");
  };

  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      setImagePreview(URL.createObjectURL(blob));
      setCaption("");

      playSound("/shutter.mp3");
      speak("Image captured. Generating caption now.");
      generateCaption(blob);
    }, "image/jpeg");
  };

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === "c") startCamera();
      else if (event.key === "Escape") stopCamera();
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div style={containerStyle}>
      <h2 style={headerStyle}>üì∏ VisionMate - AI Image Captioning</h2>

      {!cameraActive ? (
        <button onClick={startCamera} style={buttonStyle}>
          üì∑ Open Camera
        </button>
      ) : (
        <>
          <video ref={videoRef} autoPlay playsInline style={videoStyle}></video>
          <canvas ref={canvasRef} style={{ display: "none" }}></canvas>
          <button onClick={captureImage} style={buttonStyle}>
            üì∏ Capture Image
          </button>
          <button
            onClick={stopCamera}
            style={{ ...buttonStyle, backgroundColor: "#ff4d4d" }}
          >
            ‚ùå Close Camera
          </button>
        </>
      )}

      {errorMessage && (
        <p style={{ color: "red", marginTop: "10px" }}>{errorMessage}</p>
      )}

      {imagePreview && (
        <div style={{ marginTop: "20px" }}>
          <h4 style={subHeaderStyle}>üìå Preview:</h4>
          <img src={imagePreview} alt="Captured" style={imageStyle} />
        </div>
      )}

      {caption && (
        <p style={captionStyle}>
          üìú Caption: {caption}
        </p>
      )}
    </div>
  );
}

// Styles (unchanged from previous version)
const containerStyle = {
  textAlign: "left",
  padding: "10px",
  maxWidth: "100%",
  margin: "0 auto",
  minHeight: "100vh",
  boxSizing: "border-box",
};

const headerStyle = {
  fontSize: "clamp(1.5rem, 5vw, 2rem)",
  marginBottom: "15px",
};

const subHeaderStyle = {
  fontSize: "clamp(1rem, 4vw, 1.25rem)",
};

const buttonStyle = {
  display: "block",
  width: "100%",
  margin: "10px 0",
  padding: "12px 15px",
  fontSize: "clamp(14px, 4vw, 16px)",
  fontWeight: "bold",
  backgroundColor: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  touchAction: "manipulation",
};

const videoStyle = {
  width: "100%",
  height: "auto",
  maxHeight: "50vh",
  borderRadius: "10px",
  border: "2px solid #000",
  marginBottom: "10px",
  objectFit: "cover",
};

const imageStyle = {
  width: "100%",
  height: "auto",
  maxWidth: "300px",
  borderRadius: "10px",
  border: "2px solid #000",
};

const captionStyle = {
  marginTop: "20px",
  fontSize: "clamp(16px, 4.5vw, 18px)",
  fontWeight: "bold",
  color: "#007bff",
  wordWrap: "break-word",
};

export default App;
